Snippet.Code:
        int $Title$_write_coef(int *p_coef, int ntaps, int addr)
{
  int i = 0;
  int* p_addr = (int*)addr;

  LS_PRINT_FMT("TGT> writting FIR Filter Coeffients[%d] address %x \n", ntaps, addr);

  for(i = 0; i < ntaps; i++ )
  {
    LS_WRITE_INT(p_addr++, p_coef[i]);
  }
  LS_PRINT_FMT("TGT> write complete. the end address is %x \n", p_addr);
  return i;
}
