int $Title$_create_and_write_lut(int amplitude, int nLutSize, int addr)
{
  int i = 0;
  int* p_addr = (int*)addr;

  LS_PRINT_FMT("TGT> generating and writting NCO LUT values [%d] starting address %x \n", nLutSize, addr);

		while (i < nLutSize) {
			float sin_input = 2 * PI * i / nLutSize;
			int rounded_value = round(amplitude * sin(sin_input));
			LS_WRITE_INT(p_addr++, rounded_value);
			i++;
		}

  LS_PRINT_FMT("TGT> write complete. the end address is %x \n", p_addr);
  return i;
}
